import React, {Component} from 'react';
import './App.css';
import Auth from './Auth/Auth'

// import axios from 'axios'
import axios from 'axios';
axios.defaults.xsrfCookieName = 'csrftoken'
axios.defaults.xsrfHeaderName = 'X-CSRFToken'
class App extends Component{

    async authHandler() {
        let requestOptions1 = {
            method: 'GET',
        };
        // let temp;
        fetch("http://127.0.0.1:8000/test1/", requestOptions1)
            .then(response => console.log(response.headers))
        console.log(document.cookie)
        let csrftoken = this.getCookie('csrftoken')
        let urlencoded = new URLSearchParams();
        urlencoded.append("username", "first");
        urlencoded.append("password", "w8$jdP#u7LMFRJn");
        let requestOptions = {
            credentials: 'include',
            method: 'POST',
            mode: 'same-origin',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'X-CSRFToken': csrftoken
            },
            body: urlencoded
        };

        axios("http://127.0.0.1:8000/test1/", requestOptions)
        //     .then(response => response.text())
            .then(result => console.log(result.date))
    }
    render() {
        return (
            <div className="App">
                <Auth authHandler={this.authHandler}/>
            </div>
        );
    }
}

export default App;
