import React, {Component} from 'react';
import './App.css';
import Auth from './Auth/Auth'
// import axios from 'axios'

class App extends Component{
    getCookie(name){
        let cookieValue = null;
        if (document.cookie && document.cookie !== '') {
            let cookies = document.cookie.split(';');
            for (let i = 0; i < cookies.length; i++) {
                let cookie = cookies[i].trim();
                // Does this cookie string begin with the name we want?
                if (cookie.substring(0, name.length + 1) === (name + '=')) {
                    cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                    break;
                }
            }
        }
        return cookieValue;
    }
    async authHandler() {
        let requestOptions1 = {
            method: 'GET',
        };
        fetch("http://127.0.0.1:8000/test1/", requestOptions1)
            .then(response => console.log(response.headers.json()))
        let urlencoded = new URLSearchParams();

        urlencoded.append("username", "first");
        urlencoded.append("password", "w8$jdP#u7LMFRJn");
        let requestOptions = {
            method: 'POST',
            body: urlencoded,
            redirect: 'follow',

        };
        fetch("http://127.0.0.1:8000/test1/", requestOptions)
            // .then(response => response.text())
            .then(result => console.log(result.date))
    }
    render() {
        return (
            <div className="App">
                <Auth authHandler={this.authHandler}/>
            </div>
        );
    }
}

export default App;
