import React, { Component } from 'react';
import './App.css';
import Auth from './Auth/Auth'
import {Route} from 'react-router-dom'
import {Redirect} from 'react-router-dom'
// import PersArea from "./homePage/PersArea";
//
// import axios from 'axios'
class  App extends Component{
    state = {
        login:'',
        password:'',
        valigForm:false,
        token:'',
        cabinet:{}
    }

    changeLogin = async (event) =>{//считываем логин
        await this.setState({
            login:event.target.value
        })
        // console.log(this.state.login)
        console.log(event.target.value)
    }
    changePassword = (event) =>{//считываем пароль
        // this.setState({
        //     password:event.target.value
        // })
        // console.log(this.state.password)
        console.log(event.target.value)
    }
    authHandler = async () =>{
        let formdata = new FormData();
        // formdata.append("username", this.state.login);
        formdata.append("username", 'admin');
        formdata.append("password",'admin');
        // formdata.append("password",this.state.password);
        let requestOptions = {
            method: 'POST',
            body: formdata,
            redirect: 'follow'
        };
        await fetch("http://127.0.0.1:8000/token/", requestOptions)
            .then(response => response.text())
            .then(result => this.setState({token: result.split(',')[1].split(':')[1].replace('"', '').replace('"', '').replace('}', '')}))
            .catch(() => this.setState({token:''}));
        // console.log(this.state.token)
        if(this.state.token ===''){
            alert('incorect')
        }
        else{
            console.log('dssd')
        }
    }
    render() {
        const temp = () =>{
            return(<div>
                <h1>Hello</h1>
            </div>)
        }
        const tempAuth = ()=>{
            if (this.state.token !== '')
                return <Redirect to='/temp'/>
            return <Auth authHandler = {this.authHandler} changeLogin = {this.changeLogin} changePassword = {this.changePassword}/>;
        };
        return ( 
           <div className = 'App' >
               <Route path='/' exact component={tempAuth} />
               <Route path='/temp' exact component={temp}/>
           </div >
             // <div className={App}>
             //     <PersArea/>
             // </div>
        );
    }
}

export default App;
